mapper.update:
  
	update cart 
	set quantity = quantity + #{quantity }
	where productoption_no =#{productOptionNo }
	and user_no = #{userNo }

  
	update cart 
	set quantity = quantity + #{quantity }
	where productoption_no =#{productOptionNo }
	and nonuser_no = #{nonUserNo }

mapper.select:
  
	select count(*)
	from cart
	where productoption_no = #{productOptionNo }
	and user_no = #{userNo }

  
	select count(*)
	from cart
	where productoption_no = #{productOptionNo }
	and nonuser_no = #{nonUserNo }

mapper.delete:
 delete from cart where no in ( 


	  #{no.no } 


 )

 delete from cart 
	where user_no = #{userNo }
	and productoption_no in (  


	  #{list.productOptionNo } 

 )

 delete from cart 
	where nonuser_no = #{nonUserNo }
	and productoption_no in (  


	  #{list.productOptionNo } 

 )
